Field.CSS:
.sb-field-inner:has(~ .sb-field-error) .sb-field-input{
  background-color: "[theme: errorBackground]";
  border-color: #d44646;
}
.sb-field-error {
  font-size: 10pt;
}
.sb-field-label:has(~ .sb-field-error) {
  color: "[theme: errorText]";
}
.sb-field-inner:has(~ .sb-field-error) input::placeholder {
  color: "[theme: errorText]";
}
